CREATE TABLE account (id INT AUTO_INCREMENT, wallet_id INT NOT NULL, type_id INT NOT NULL, name TEXT NOT NULL, description TEXT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX wallet_id_idx (wallet_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE account_type (id INT AUTO_INCREMENT, name TEXT NOT NULL, description TEXT, icon LONGBLOB, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE currency (id INT AUTO_INCREMENT, name TEXT NOT NULL, description TEXT, exchange FLOAT(18, 2) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE periodic_transaction (id INT AUTO_INCREMENT, account_id INT NOT NULL, currency_id INT NOT NULL, name TEXT, description TEXT, amount FLOAT(18, 2) NOT NULL, date DATETIME NOT NULL, picture LONGBLOB, geo TEXT, periodicity INT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX account_id_idx (account_id), INDEX currency_id_idx (currency_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE report (id INT AUTO_INCREMENT, wallet_id INT NOT NULL, name TEXT NOT NULL, description TEXT, date_from DATETIME NOT NULL, date_to DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX wallet_id_idx (wallet_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE transaction (id INT AUTO_INCREMENT, account_id INT NOT NULL, currency_id INT NOT NULL, name TEXT NOT NULL, description TEXT NOT NULL, amount FLOAT(18, 2) NOT NULL, date DATETIME NOT NULL, picture LONGBLOB, geo TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX account_id_idx (account_id), INDEX currency_id_idx (currency_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE user (id INT AUTO_INCREMENT, username VARCHAR(255) NOT NULL, password VARCHAR(255) NOT NULL, hash VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE wallet (id INT AUTO_INCREMENT, user_id INT NOT NULL, name TEXT NOT NULL, description TEXT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE account ADD CONSTRAINT account_wallet_id_wallet_id FOREIGN KEY (wallet_id) REFERENCES wallet(id) ON DELETE CASCADE;
ALTER TABLE periodic_transaction ADD CONSTRAINT periodic_transaction_currency_id_currency_id FOREIGN KEY (currency_id) REFERENCES currency(id) ON DELETE CASCADE;
ALTER TABLE periodic_transaction ADD CONSTRAINT periodic_transaction_account_id_account_id FOREIGN KEY (account_id) REFERENCES account(id) ON DELETE CASCADE;
ALTER TABLE report ADD CONSTRAINT report_wallet_id_wallet_id FOREIGN KEY (wallet_id) REFERENCES wallet(id) ON DELETE CASCADE;
ALTER TABLE transaction ADD CONSTRAINT transaction_currency_id_currency_id FOREIGN KEY (currency_id) REFERENCES currency(id) ON DELETE CASCADE;
ALTER TABLE transaction ADD CONSTRAINT transaction_account_id_account_id FOREIGN KEY (account_id) REFERENCES account(id) ON DELETE CASCADE;
ALTER TABLE wallet ADD CONSTRAINT wallet_user_id_user_id FOREIGN KEY (user_id) REFERENCES user(id) ON DELETE CASCADE;
